mapper.update:
UPDATE dc_sys_operate
id = #{id}, menuId = #{menuId}, url = #{url}, operateName = #{operateName}, operateCode = #{operateCode}, orderNo = #{orderNo}, createDate = #{createDate}, createdBy = #{createdBy}, updateDate = #{updateDate}, updatedBy = #{updatedBy}
WHERE id = #{id}

mapper.select:
SELECT o.id, m.id AS menuId, m.menuName, o.operateCode, o.operateName, o.url, o.orderNo, DATE_FORMAT(o.updateDate, '%Y-%m-%d %H:%i:%S') as updateDate, o.updatedBy, u.uname as updator FROM dc_sys_operate o LEFT JOIN dc_sys_menu m ON o.menuId = m.id left join dc_user u on o.updatedBy=u.id where 1=1
and o.menuId=#{menuId}
and o.operateName like '%${operateName}%'
ORDER BY o.menuId, o.orderNo limit #{start},#{pageSize}
